                                                    <Box sx={{ flexGrow: 1 }}>
                                                        <Grid container>
                                                            <Grid size={12}>
                                                                <Typography className={classes.label}>Nome</Typography>
                                                            </Grid>
                                                            <Grid size={12}>
                                                                <TextField
                                                                    error={error}
                                                                    fullWidth
                                                                    name="name"
                                                                    value={formValues.nome}
                                                                    onChange={handleInputChange}
                                                                    size='small'
                                                                    variant="outlined"
                                                                    className={classes.textField}
                                                                />
                                                            </Grid>
                                                        </Grid>
                                                    </Box>
                                                </Grid>
