                                                                <Typography className={classes.label}>Razão social</Typography>
                                                            </Grid>
                                                            <Grid size={12}>
                                                                <TextField
                                                                    error={error}

                                                                    fullWidth
                                                                    name="identity"
                                                                    value={formValues.razaoSocial}
                                                                    onChange={handleInputChange}
                                                                    size='small'
                                                                    variant="outlined"
                                                                    className={classes.textField}
                                                                />
                                                            </Grid>
                                                        </Grid>
                                                    </Box>
                                                </Grid>
                                                <Grid size={{ xs: 12, md: 4 }}>
                                                    <Box sx={{ flexGrow: 1 }}>
